pragma solidity ^0.5.0;

import "./ERC20Interface.sol";

contract ERC20Wallet {
    /*
        Reads ETH balance of the _owner
    */
    function readEthBalance(address _owner) public view returns (uint256 balance) {
        return address(_owner).balance;
    }

    /*
        Reads balance of the _owner
    */
    function readBalance(address _tokenAddress) public view returns (uint256 balance) {
        ERC20Interface token = ERC20Interface(_tokenAddress);
        return token.balanceOf(msg.sender);
    }

    /*
        Allows this smart contract to spend on behalf of msg.sender
    */
    function approve(address _tokenAddress, uint256 _value) public returns (bool success) {
        ERC20Interface token = ERC20Interface(_tokenAddress);
        return token.approve(address(this), _value);
    }
}
